mapper.select:
SELECT BOARD_ID as boardId , MEMBER_ID as memberId, MNAME, WRITE_DATE as writeDate, MODIFY_DATE as modifyDate, TITLE, CONTENT, EVALUATION FROM BOARD d,MEMBERS m WHERE d.MEMBER_ID=m.mno
AND d.CATEGORY_ID=#{category_id}
ORDER BY BOARD_ID DESC LIMIT #{start},#{page}

mapper.update:
UPDATE BOARD 
SET
	TITLE=#{title},
	CONTENT=#{content}
WHERE
	BOARD_ID=#{boardId}
